import React from "react";
import gql from "graphql-tag";
import { useQuery } from "@apollo/react-hooks";
import ErrorMessage from "./ErrorMessage";
import styled from "styled-components";

const SingleComposerStyles = styled.div`
  max-width: 1200px;
  margin: 2rem auto;
  box-shadow: ${props => props.theme.bs};
  display: grid;
  grid-auto-columns: 1fr;
  grid-auto-flow: column;
  min-height: 800px;
  .name {
    text-align: center;
  }
  .details {
    margin: 3rem;
    font-size: 2rem;
  }
`;

const SINGLE_COMPOSER_QUERY = gql`
  query SINGLE_COMPOSER_QUERY($id: ID!) {
    composer(where: { id: $id }) {
      id
      name
      songs {
        id
        title
      }
    }
  }
`;

const SingleComposer = ({ id }) => {
  const { loading, error, data } = useQuery(SINGLE_COMPOSER_QUERY, {
    variables: { id }
  });
  if (error) return <ErrorMessage error={error} />;
  if (loading) return <p>Loading...</p>;
  if (!data.compsoer) return <p>No composer found for {id}</p>;
  // console.log(data.song);
  const { name, songs } = data.composer;
  return (
    <SingleComposerStyles>
      <div className="details">
        <h1 className="name">{name}</h1>
        {songs && (
          <h2>
            {name} has either written or co-written these songs:{" "}
            {songs.map(song => (
              <p key={song.id}>{song.title}</p>
            ))}
          </h2>
        )}
      </div>
    </SingleComposerStyles>
  );
};

export default SingleComposer;
