import React, { useState } from "react";
import { useQuery, useMutation } from "@apollo/react-hooks";
import Router from "next/router";
import { gql } from "apollo-boost";
import styled from "styled-components";
import Album from "./Album";
import Pagination from "../components/Pagination";
import { perPage } from "../config";
import { SelectButton } from "primereact/selectbutton";
import { selectButtonsAthruZWithNumber } from "../lib/selectButtons";
import { Dropdown } from "primereact/dropdown";
import { Button } from "primereact/button";
import { MultiList, Center } from "./Artists";

export const StyledDropdown = styled(Dropdown)`
  width: 50%;
  @media screen and (max-width: 600px) {
    width: 100%;
  }
`;

export const ALL_SINGLES_QUERY = gql`
  query ALL_SINGLES_QUERY($first: Int = ${perPage}, $skip: Int = 0, $sortBy: AlbumOrderByInput!) {
    albums(where: {single: true}, first: $first, skip: $skip, orderBy: $sortBy ) {
      id
      title
      recordLabel
      releaseDate
      format
      catalogNumber
      single
      artists {
        id
        name
      }
    }
  }
`;

export const ALL_SINGLES_BEGINNING_WITH_QUERY = gql`
  query ALL_SINGLES_BEGINNING_WITH_QUERY($skip: Int = 0, $first: Int = ${perPage}, $letter: String, $sortBy: AlbumOrderByInput!) {
    albums(where: {single: true, title_starts_with: $letter}, first: $first, skip: $skip, orderBy: $sortBy) {
      id
      title
      recordLabel
      releaseDate
      format
      catalogNumber
      single
      artists {
        id
        name
      }
    }
  }
`;

export const ALL_SINGLES_BEGINNING_WITH_NUMBER_QUERY = gql`
  query ALL_SINGLES_BEGINNING_WITH_NUMBER_QUERY($skip: Int = 0, $first: Int = ${perPage}, $sortBy: AlbumOrderByInput!) {
    albums(where: {single: true, OR: [{title_starts_with: "0"},{title_starts_with: "1"},{title_starts_with: "2"},{title_starts_with: "3"},{title_starts_with: "4"},{title_starts_with: "5"},{title_starts_with: "6"},{title_starts_with: "7"},{title_starts_with: "8"},{title_starts_with: "9"}]}, first: $first, skip: $skip, orderBy: $sortBy) {
      id
      title
      recordLabel
      releaseDate
      format
      catalogNumber
      single
      artists {
        id
        name
      }
    }
  }
`;

const TURN_SINGLE_INTO_ALBUM_MUTATION = gql`
  mutation TURN_SINGLE_INTO_ALBUM_MUTATION($id: ID!) {
    turnSingleIntoAlbum(id: $id) {
      id
      title
      single
    }
  }
`;

const ADD_SONGS_FROM_ONE_SINGLE_TO_ANOTHER_MUTATION = gql`
  mutation ADD_SONGS_FROM_ONE_SINGLE_TO_ANOTHER_MUTATION($albumOneId: ID!, $albumTwoId: ID!) {
    addSongToAlbum(albumOneId: $albumOneId, albumTwoId: $$albumTwoId) {
      id
      title
    }
  }
`;

const sortBySelectItems = [
  { label: "Title, ascending", value: "title_ASC" },
  { label: "Title, descending", value: "title_DESC" },
  { label: "Format, ascending", value: "format_DESC" },
  { label: "Format, descending", value: "format_ASC" },
  { label: "Release Date, ascending", value: "releaseDate_ASC" },
  { label: "Release Date, descending", value: "releaseDate_DESC" },
  { label: "Record Label, ascending", value: "recordLabel_ASC" },
  { label: "Record Label, descending", value: "recordLabel_DESC" },
  { label: "Catalog Number, ascending", value: "catalogNumber_ASC" },
  { label: "Catalog Number, descending", value: "catalogNumber_DESC" },
];

const Singles = ({ page }) => {
  const [letter, setLetter] = useState("");
  const [sortBy, setSortBy] = useState("title_ASC");
  const [checkedItems, setCheckedItems] = useState([]);

  const [
    turnSingleIntoAlbumMutation,
    { loading: turnSingleIntoAlbumLoading, error: turnSingleIntoAlbumError },
  ] = useMutation(TURN_SINGLE_INTO_ALBUM_MUTATION);

  const [
    addSongToAlbumMutation,
    { loading: addSongToAlbumLoading, error: addSongToAlbumError },
  ] = useMutation(ADD_SONGS_FROM_ONE_SINGLE_TO_ANOTHER_MUTATION);

  const {
    loading: allSinglesLoading,
    error: allSinglesError,
    data: allSinglesData,
  } = useQuery(ALL_SINGLES_QUERY, {
    variables: { skip: page * perPage - perPage, first: perPage, sortBy },
  });
  const {
    loading: singlesBeginningWithLoading,
    error: singlesBeginningWithError,
    data: singlesBeginningWithData,
  } = useQuery(ALL_SINGLES_BEGINNING_WITH_QUERY, {
    variables: {
      skip: page * perPage - perPage,
      first: perPage,
      letter,
      sortBy,
    },
  });
  const {
    loading: singlesBeginningWithNumberLoading,
    error: singlesBeginningWithNumberError,
    data: singlesBeginningWithNumberData,
  } = useQuery(ALL_SINGLES_BEGINNING_WITH_NUMBER_QUERY, {
    variables: {
      skip: page * perPage - perPage,
      first: perPage,
      sortBy,
    },
  });
  if (
    singlesBeginningWithLoading ||
    allSinglesLoading ||
    singlesBeginningWithNumberLoading
  )
    return <p>Loading...</p>;
  if (
    singlesBeginningWithError ||
    allSinglesError ||
    singlesBeginningWithNumberError
  )
    return <p>Error :(</p>;

  async function turnSingleIntoAlbum() {
    turnSingleIntoAlbumMutation({ variables: { id: checkedItems[0] } });
    turnSingleIntoAlbumError && console.log(turnSingleIntoAlbumError);
    setCheckedItems([]);
  }

  async function joinASideAndBSide() {
    addSongToAlbumMutation({
      variables: { albumOneId: checkedItems[0], albumTwoId: checkedItems[1] },
    });
  }

  function getSingles() {
    if (letter === "" || !letter) {
      return allSinglesData.albums
        .filter((album) => album.single)
        .map((album) => (
          <Album
            addToCheckedItemsList={setCheckedItems}
            checkedItems={checkedItems}
            key={album.id}
            album={album}
          />
        ));
    } else if (letter === "#") {
      return singlesBeginningWithNumberData.albums
        .filter((album) => album.single)
        .map((single) => (
          <Album
            key={single.id}
            album={single}
            addToCheckedItemsList={setCheckedItems}
            checkedItems={checkedItems}
          />
        ));
    } else {
      return singlesBeginningWithData.albums
        .filter((album) => album.single)
        .map((single) => (
          <Album
            key={single.id}
            album={single}
            addToCheckedItemsList={setCheckedItems}
            checkedItems={checkedItems}
          />
        ));
    }
  }

  return (
    <Center>
      <Button
        onClick={() => turnSingleIntoAlbum()}
        label="TURN SINGLE INTO ALBUM"
        style={{ marginRight: ".25em" }}
      />
      <Button
        label="JOIN A-SIDE AND B-SIDE"
        onClick={() => joinASideAndBSide()}
        style={{ marginRight: ".25em" }}
      />
      <ul>
        Checked items:{" "}
        {checkedItems && checkedItems.map((item) => <li key={item}>{item}</li>)}
      </ul>
      <SelectButton
        value={letter}
        options={selectButtonsAthruZWithNumber}
        onChange={(e) => {
          setLetter(e.value);
          Router.push({ pathname: "/singles", query: { page: 1 } });
        }}
      />
      <br />
      <StyledDropdown
        value={sortBy}
        options={sortBySelectItems}
        onChange={(e) => setSortBy(e.value)}
        placeholder="Sort Byâ€¦"
      />
      <br />
      <Pagination page={page} model="singles" letter={letter} />
      <MultiList>{getSingles()}</MultiList>
      <Pagination page={page} model="singles" letter={letter} />
    </Center>
  );
};

export default Singles;
