import React from "react";
import { useQuery } from "@apollo/react-hooks";
import moment from "moment";

import Link from "next/link";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { CURRENT_USER_QUERY } from "./Nav";

function formattedDate(date) {
  console.log(moment(date).dayOfYear());
  // if the record was released on Jan 1, it probably wasn't and only
  // the year was available.  So we return true and format just the year.
  if (moment(date).month() === 0 && moment(date).dayOfYear() === 1) {
    console.log("yes");
    return moment(date).format("YYYY");
  }
  return moment(date).format("MMMM Do, YYYY");
}

const Album = ({ album }) => {
  const { loading, error, data } = useQuery(CURRENT_USER_QUERY);
  const footer = data.me ? (
    <span>
      <Link href={{ pathname: "/update", query: { id: album.id } }}>
        <a>
          <Button
            label="Edit"
            icon="pi pi-pencil"
            style={{ marginRight: ".25em" }}
          />
        </a>
      </Link>
    </span>
  ) : null;
  const title = (
    <span>
      <Link href={{ pathname: "/album", query: { id: album.id } }}>
        <a>{album.title}</a>
      </Link>
    </span>
  );

  return (
    <Card
      title={title}
      footer={footer}
      subTitle={
        album.releaseDate
          ? `released ${formattedDate(album.releaseDate)}`
          : "no release date information"
      }
    />
  );
};

