import React, { useState } from "react";
import gql from "graphql-tag";
import { useQuery } from "@apollo/react-hooks";
import ErrorMessage from "./ErrorMessage";
import styled from "styled-components";
import Link from "next/link";
import { Card } from "primereact/card";
const SingleSongStyles = styled.div`
  max-width: 1200px;
  margin: 2rem auto;
  box-shadow: ${(props) => props.theme.bs};
  display: grid;
  grid-auto-columns: 1fr;
  grid-auto-flow: column;
  min-height: 800px;
  .title {
    text-align: center;
  }
  .details {
    margin: 3rem;
    font-size: 2rem;
  }
`;

const SINGLE_SONG_QUERY = gql`
  query SINGLE_SONG_QUERY($id: ID!) {
    song(where: { id: $id }) {
      id
      title
      composer {
        id
        name
      }
      publisher
      publishedDate
      altTitle
      instrumental
      fromFilm
      tribute
      artists {
        id
        name
      }
      albums {
        id
        title
      }
      recordings {
        id
        producer
        location
        completed
      }
    }
  }
`;

const SingleSong = ({ id }) => {
  const { loading, error, data } = useQuery(SINGLE_SONG_QUERY, {
    variables: { id },
  });
  const [layout, setLayout] = useState("list");
  if (error) return <ErrorMessage error={error} />;
  if (loading) return <p>Loading...</p>;
  if (!data.song) return <p>No song found for {id}</p>;
  // console.log(data.song);
  const {
    title,
    composer,
    publisher,
    publishedDate,
    altTitle,
    instrumental,
    fromFilm,
    tribute,
    artists,
    albums,
    recordings,
  } = data.song;

  function itemTemplate(song, layout) {
    if (layout === "list") {
      return (
        <div className="p-grid">
          <div>{song.title}</div>
        </div>
      );
    }
    if (layout === "grid") {
      return (
        <div className="p-col-12 p-md-3">
          <div>{song.title}</div>
        </div>
      );
    }
  }
  return (
    <Card title={title} subTitle={composer}></Card>

    //   <SingleSongStyles>
    //     <div className="details">
    //       <h1 className="title">{title}</h1>
    //       {composer &&
    //         composer.map((comp) => (
    //           <h2 key={comp.id}>
    //             <Link href={{ pathname: "composer", query: { id: comp.id } }}>
    //               <a>Composer: {comp.name}</a>
    //             </Link>
    //           </h2>
    //         ))}
    //       {publisher && <h2>Publisher: {publisher}</h2>}
    //       {publishedDate && <h2>Published Date: {publishedDate}</h2>}
    //       {altTitle && <h2>Alt Title: {altTitle}</h2>}
    //       {instrumental && <h2>Instrumental</h2>}
    //       {fromFilm && <h2>From Film: {fromFilm}</h2>}
    //       {tribute && <h2>{tribute}</h2>}
    //       {artists && (
    //         <h2>
    //           Performed by:{" "}
    //           {artists.map((artist) => (
    //             <p key={artist.id}>
    //               <Link href={{ pathname: "artist", query: { id: artist.id } }}>
    //                 <a>{artist.name}</a>
    //               </Link>
    //             </p>
    //           ))}
    //         </h2>
    //       )}
    //       {albums && (
    //         <h2>
    //           Can be heard on the albums:{" "}
    //           {albums.map((album) => (
    //             <p key={album.id}>
    //               <Link href={{ pathname: "album", query: { id: album.id } }}>
    //                 <a>{album.title}</a>
    //               </Link>
    //             </p>
    //           ))}
    //         </h2>
    //       )}
    //     </div>
    //   </SingleSongStyles>
    // );
  );
};

export default SingleSong;
