import React from "react";
import Link from "next/link";
import NavStyles from "./styles/navStyles";
import { useQuery } from "@apollo/react-hooks";
import { useRouter } from "next/router";
import gql from "graphql-tag";
import Signout from "./Signout";
import styled from "styled-components";

export const CURRENT_USER_QUERY = gql`
  query {
    me {
      id
      email
      name
      permissions
    }
  }
`;

const StyledA = styled.a`
  color: ${(props) => (props.active ? props.theme.blue : props.theme.black)};
`;

const Nav = () => {
  const { loading, error, data } = useQuery(CURRENT_USER_QUERY);
  const router = useRouter();
  console.log(router.pathname);
  if (loading) return "Loading...";
  if (error) return `Error! ${error.message}`;
  const { me } = data;

  return (
    <NavStyles>
      {me && <p>{me.name}</p>}

      <Link href="/songs">
        <StyledA active={router.pathname === "/songs"}>Songs</StyledA>
      </Link>
      <Link href="/artists">
        <StyledA active={router.pathname === "/artists"}>Artists</StyledA>
      </Link>
      <Link href="/albums">
        <StyledA active={router.pathname === "/albums"}>Albums</StyledA>
      </Link>
      <Link href="/composers">
        <StyledA active={router.pathname === "/composers"}>Composers</StyledA>
      </Link>
      {me && <Signout />}
    </NavStyles>
  );
};

export default Nav;
