import React, { Component, useState } from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import styled from "styled-components";
import Song from "../components/Song";
import Pagination from "../components/Pagination";
import { perPage } from "../config";
import { SelectButton } from "primereact/selectbutton";
import { selectButtonsAthruZ } from "../lib/selectButtons";

export const ALL_SONGS_QUERY = gql`
  query ALL_SONGS_QUERY($skip: Int = 0, $first: Int = ${perPage}) {
    songs(first: $first, skip: $skip, orderBy: title_ASC ) {
      id
      title
      publisher
      publishedDate
      composer {
        name
        id
      }
    }
  }
`;

export const ALL_SONGS_BEGINNING_WITH_QUERY = gql`
  query ALL_SONGS_BEGINNING_WITH_QUERY($skip: Int = 0, $first: Int = ${perPage}, $letter: String) {
    songs(where: {title_starts_with: $letter}, first: $first, skip: $skip, orderBy: title_ASC) {
      id
      title
      publisher
      publishedDate
      composer {
        name
        id
      }
    }
  }
`;

const Center = styled.div`
  text-align: center;
`;

const SongsList = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 60px;
  max-width: ${(props) => props.theme.maxWidth};
  margin: 0 auto;
`;

const Songs = ({ page }) => {
  const [letter, setLetter] = useState("");
  const {
    loading: allSongsLoading,
    error: allSongsError,
    data: allSongsData,
  } = useQuery(ALL_SONGS_QUERY, {
    variables: { skip: page * perPage - perPage, first: perPage },
  });
  const {
    loading: songsBeginningWithLoading,
    error: songsBeginningWithError,
    data: songsBeginningWithData,
  } = useQuery(ALL_SONGS_BEGINNING_WITH_QUERY, {
    variables: { skip: page * perPage - perPage, first: perPage, letter },
  });
  if (allSongsLoading || songsBeginningWithLoading) return <p>Loading...</p>;
  if (allSongsError || songsBeginningWithError) return <p>Error :(</p>;

  const songs = !letter
    ? allSongsData.songs.map((song) => <Song key={song.id} song={song} />)
    : songsBeginningWithData.songs.map((song) => (
        <Song key={song.id} song={song} />
      ));

  return (
    <Center>
      <SelectButton
        value={letter}
        options={selectButtonsAthruZ}
        onChange={(e) => setLetter(e.value)}
      />
      <Pagination page={page} model="songs" letter={letter} />
      <SongsList>{songs}</SongsList>
      <Pagination page={page} model="songs" letter={letter} />
    </Center>
  );
};

export default Songs;
