import React, { useState } from "react";
import { useQuery } from "@apollo/react-hooks";
import formatDate from "../lib/formatDate";
import Link from "next/link";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { CURRENT_USER_QUERY } from "./Nav";
import { Checkbox } from "primereact/checkbox";
import styled from "styled-components";

const StyledFooter = styled.div`
  display: flex;
  justify-content: space-evenly;
`;

const StyledFlexStart = styled.div`
  align-self: center;
`;

const StlyedFlexEnd = styled.div`
  align-self: flex-end;
`;

const Album = ({ album, addToCheckedItemsList, checkedItems }) => {
  const { loading, error, data } = useQuery(CURRENT_USER_QUERY);
  const [checked, setIsChecked] = useState(false);
  function handleChecked(e) {
    setIsChecked(e.checked);
    if (e.checked) {
      addToCheckedItemsList([...checkedItems, album.id]);
    }
    if (!e.checked) {
      console.log("you unchecked it");
    }
  }

  const footer = data.me ? (
    <StyledFooter>
      <StyledFlexStart>
        <Link href={{ pathname: "/updatealbum", query: { id: album.id } }}>
          <a>
            <Button
              label="Edit"
              icon="pi pi-pencil"
              style={{ marginRight: ".25em" }}
            />
          </a>
        </Link>
      </StyledFlexStart>
      <StlyedFlexEnd>
        <Checkbox onChange={(e) => handleChecked(e)} checked={checked}>
          combine
        </Checkbox>
      </StlyedFlexEnd>
    </StyledFooter>
  ) : null;
  const title = (
    <span>
      <Link href={{ pathname: "/album", query: { id: album.id } }}>
        <a>{album.title || "no title"}</a>
      </Link>
    </span>
  );
  const artists =
    album.artists &&
    album.artists.map((artist, index) => (
      <span key={artist.name}>
        <Link href={{ pathname: "/artist", query: { id: artist.id } }}>
          <a>{artist.name}</a>
        </Link>
        {album.artists.length === index + 1 ? " " : ", "}
      </span>
    ));
  return (
    <Card
      title={title}
      footer={footer}
      subTitle={
        album.releaseDate
          ? `released ${formatDate(album.releaseDate)}`
          : "no release date information"
      }
    >
      {album.single && <p>Single</p>}
      {album.format && <p>Format: {album.format}</p>}
      {album.catalogNumber && <p>Catalog #: {album.catalogNumber}</p>}
      {artists}
    </Card>
  );
};

export default Album;
