import React, { useState } from "react";
import gql from "graphql-tag";
import { useQuery } from "@apollo/react-hooks";
import ErrorMessage from "./ErrorMessage";
import styled from "styled-components";
import Link from "next/link";
import { Card } from "primereact/card";
import formatDate from "../lib/formatDate";
const SingleSongStyles = styled.div`
  max-width: 1200px;
  margin: 2rem auto;
  box-shadow: ${(props) => props.theme.bs};
  display: grid;
  grid-auto-columns: 1fr;
  grid-auto-flow: column;
  min-height: 800px;
  .title {
    text-align: center;
  }
  .details {
    margin: 3rem;
    font-size: 2rem;
  }
`;

const TitleCard = styled(Card)`
  text-align: center;
`;

const PerformedByAndRecordingspane = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
`;

const SINGLE_SONG_QUERY = gql`
  query SINGLE_SONG_QUERY($id: ID!) {
    song(where: { id: $id }) {
      id
      title
      composer {
        id
        name
      }
      publisher
      publishedDate
      altTitle
      instrumental
      fromFilm
      tribute
      artists {
        id
        name
      }
      albums {
        id
        title
      }
      recordings {
        id
        producer
        location
        completed
        albums {
          id
          title
        }
        song {
          id
          title
        }
      }
    }
  }
`;

const SingleSong = ({ id }) => {
  const { loading, error, data } = useQuery(SINGLE_SONG_QUERY, {
    variables: { id },
  });
  const [layout, setLayout] = useState("list");
  if (error) return <ErrorMessage error={error} />;
  if (loading) return <p>Loading...</p>;
  if (!data.song) return <p>No song found for {id}</p>;
  // console.log(data.song);
  const {
    title,
    composer,
    publisher,
    publishedDate,
    altTitle,
    instrumental,
    fromFilm,
    tribute,
    artists,
    albums,
    recordings,
  } = data.song;

  const composerNames = composer.map((comp, index) => {
    let trailingChar = ", ";
    if (composer && index === composer.length - 2) {
      trailingChar = "& ";
    }
    if (composer && !composer[index + 1]) {
      trailingChar = "";
    }
    return (
      <span key={comp.id}>
        <Link href={{ pathname: "/composer", query: { id: comp.id } }}>
          <a>{comp.name} </a>
        </Link>
        {trailingChar}
      </span>
    );
  });

  const albumsCards = albums.map((album) => {
    return (
      <Card
        key={album.id}
        title={
          <Link href={{ pathname: "album", query: { id: album.id } }}>
            <a>{album.title}</a>
          </Link>
        }
      >
        {renderAlbumRecordings(album)}
      </Card>
    );
  });

  // const matchedAlbumRecording = recordings.filter(recording => {
  //   recording.albums.forEach(album => (
  //     album.title ===
  //   ))
  // })

  function renderAlbumRecordings(theAlbum) {
    const matchedAlbumRecordingInfo = {};
    recordings.forEach((recording) => {
      recording.albums.forEach((album) => {
        if (album.id === theAlbum.id) {
          (matchedAlbumRecordingInfo.producer = recording.producer),
            (matchedAlbumRecordingInfo.location = recording.location),
            (matchedAlbumRecordingInfo.completed = recording.completed);
        }
      });
    });
    return (
      <ul>
        {matchedAlbumRecordingInfo.producer && (
          <li>Produced By: {matchedAlbumRecordingInfo.producer}</li>
        )}
        {matchedAlbumRecordingInfo.location && (
          <li>Recording Location: {matchedAlbumRecordingInfo.location}</li>
        )}
        {matchedAlbumRecordingInfo.completed && (
          <li>Completed: {matchedAlbumRecordingInfo.completed}</li>
        )}
      </ul>
    );
  }

  return (
    <>
      <TitleCard title={title} subTitle={composerNames}>
        {altTitle && <h3>Alt Title: {altTitle}</h3>}
        {publisher && <h3>Publisher: {publisher}</h3>}
        {publishedDate && <h3>Published Date: {formatDate(publishedDate)}</h3>}
        {instrumental && <h3>Instrumental</h3>}
        {fromFilm && <h3>From Film: {fromFilm}</h3>}
      </TitleCard>
      <PerformedByAndRecordingspane>
        {artists && (
          <Card title="Performed by: ">
            <ul style={{ listStyleType: "none", margin: 0, padding: 0 }}>
              {artists.map((artist) => (
                <li key={artist.id}>
                  <Link href={{ pathname: "artist", query: { id: artist.id } }}>
                    <a>{artist.name}</a>
                  </Link>
                </li>
              ))}
            </ul>
          </Card>
        )}
        {albums && (
          <Card
            title={`Can be heard on the album${albums.length > 1 ? "s" : ""}:`}
          >
            {albumsCards}
          </Card>
        )}
      </PerformedByAndRecordingspane>
    </>
    //   <SingleSongStyles>
    //     <div className="details">
    //       <h1 className="title">{title}</h1>
    //       {composer &&
    //         composer.map((comp) => (
    //           <h2 key={comp.id}>
    //             <Link href={{ pathname: "composer", query: { id: comp.id } }}>
    //               <a>Composer: {comp.name}</a>
    //             </Link>
    //           </h2>
    //         ))}
    //       {publisher && <h2>Publisher: {publisher}</h2>}
    //       {publishedDate && <h2>Published Date: {publishedDate}</h2>}
    //       {altTitle && <h2>Alt Title: {altTitle}</h2>}
    //       {instrumental && <h2>Instrumental</h2>}
    //       {fromFilm && <h2>From Film: {fromFilm}</h2>}
    //       {tribute && <h2>{tribute}</h2>}
    //       {artists && (
    //         <h2>
    //           Performed by:{" "}
    //           {artists.map((artist) => (
    //             <p key={artist.id}>
    //               <Link href={{ pathname: "artist", query: { id: artist.id } }}>
    //                 <a>{artist.name}</a>
    //               </Link>
    //             </p>
    //           ))}
    //         </h2>
    //       )}
    //       {albums && (
    //         <h2>
    //           Can be heard on the albums:{" "}
    //           {albums.map((album) => (
    //             <p key={album.id}>
    //               <Link href={{ pathname: "album", query: { id: album.id } }}>
    //                 <a>{album.title}</a>
    //               </Link>
    //             </p>
    //           ))}
    //         </h2>
    //       )}
    //     </div>
    //   </SingleSongStyles>
    // );
  );
};

export default SingleSong;
