import React, { Component } from "react";
import { Mutation, Query } from "react-apollo";
import gql from "graphql-tag";
import Router from "next/router";
import Form from "./styles/Form";
import Error from "./ErrorMessage";

const UPDATE_SONG_MUTATION = gql`
  mutation UPDATE_SONG_MUTATION(
    $id: ID!
    $title: String
    $publisher: String
    $publishedDate: DateTime
    $altTitle: String
    $instrumental: Boolean
    $fromFilm: String
    $tribute: String
  ) {
    updateSong(
      id: $id
      title: $title
      publisher: $publisher
      publishedDate: $publishedDate
      altTitle: $altTitle
      instrumental: $instrumental
      fromFilm: $fromFilm
      tribute: $tribute
    ) {
      id
      title
      description
      price
    }
  }
`;

class UpdateItem extends Component {
  state = {};
  handleChange = e => {
    const { name, type, value } = e.target;
    const val = type === "number" ? parseFloat(value) : value;
    this.setState({ [name]: val });
  };
  updateSong = async (e, updateSongMutation) => {
    e.preventDefault();
    console.log("Updating Song!!");
    console.log(this.state);
    const res = await updateSongMutation({
      variables: {
        id: this.props.id,
        ...this.state
      }
    });
    console.log("Updated!!");
  };

  render() {
    return (
      <Query
        query={UPDATE_SONG_MUTATION}
        variables={{
          id: this.props.id
        }}
      >
        {({ data, loading }) => {
          if (loading) return <p>Loading...</p>;
          if (!data.item) return <p>No Item Found for ID {this.props.id}</p>;
          return (
            <Mutation mutation={UPDATE_SONG_MUTATION} variables={this.state}>
              {(updateSong, { loading, error }) => (
                <Form onSubmit={e => this.updateItem(e, updateItem)}>
                  <Error error={error} />
                  <fieldset disabled={loading} aria-busy={loading}>
                    <label htmlFor="title">
                      Title
                      <input
                        type="text"
                        id="title"
                        name="title"
                        placeholder="Title"
                        required
                        defaultValue={data.song.title}
                        onChange={this.handleChange}
                      />
                    </label>

                    <label htmlFor="publisher">
                      Title
                      <input
                        type="text"
                        id="publisher"
                        name="publisher"
                        placeholder="Title"
                        required
                        defaultValue={data.song.publisher}
                        onChange={this.handleChange}
                      />
                    </label>

                    <label htmlFor="publisher">
                      Title
                      <input
                        type="text"
                        id="publisher"
                        name="publisher"
                        placeholder="Title"
                        required
                        defaultValue={data.song.publisher}
                        onChange={this.handleChange}
                      />
                    </label>

                    <label htmlFor="altTitle">
                      Title
                      <input
                        type="text"
                        id="altTitle"
                        name="altTitle"
                        placeholder="Alt Title"
                        required
                        defaultValue={data.song.altTitle}
                        onChange={this.handleChange}
                      />
                    </label>

                    <label htmlFor="altTitle">
                      Title
                      <input
                        type="text"
                        id="altTitle"
                        name="altTitle"
                        placeholder="Alt Title"
                        required
                        defaultValue={data.song.altTitle}
                        onChange={this.handleChange}
                      />
                    </label>

                    <label htmlFor="fromFilm">
                      Title
                      <input
                        type="text"
                        id="fromFilm"
                        name="fromFilm"
                        placeholder="From Film"
                        required
                        defaultValue={data.song.fromFilm}
                        onChange={this.handleChange}
                      />
                    </label>

                    <label htmlFor="tribute">
                      Title
                      <input
                        type="text"
                        id="tribute"
                        name="tribute"
                        placeholder="Tribute"
                        required
                        defaultValue={data.song.tribute}
                        onChange={this.handleChange}
                      />
                    </label>

                    <button type="submit">
                      Sav{loading ? "ing" : "e"} Changes
                    </button>
                  </fieldset>
                </Form>
              )}
            </Mutation>
          );
        }}
      </Query>
    );
  }
}

export default UpdateItem;
export { UPDATE_SONG_MUTATION };
