import React from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import styled from "styled-components";
import Artist from "./Artist";
import Pagination from "../components/Pagination";
import { perPage } from "../config";

const ALL_ARTISTS_QUERY = gql`
  query ALL_ARTISTS_QUERY($skip: Int = 0, $first: Int = ${perPage}) {
    artists(first: $first, skip: $skip, orderBy: name_ASC ) {
      id
      name
    }
  }
`;

const Center = styled.div`
  text-align: center;
`;

const ArtistsList = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 60px;
  max-width: ${props => props.theme.maxWidth};
  margin: 0 auto;
`;

const Artists = ({ page }) => {
  const { loading, error, data } = useQuery(ALL_ARTISTS_QUERY);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  const artists = data.artists.map(artist => (
    <Artist key={artist.id} artist={artist} />
  ));

  return (
    <Center>
      <Pagination page={page} model="artists" />
      <p>
        <b>I found {artists.length} artists.</b>
      </p>

      <ArtistsList>{artists}</ArtistsList>
      <Pagination page={page} model="artists" />
    </Center>
  );
};

export default Artists;
