import React from "react";
import styled from "styled-components";

const StyledButton = styled.button`
  border-radius: 10px;
  cursor: pointer;
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-3px);
  }

  background-color: ${(props) => (props.primary ? props.theme.blue : "green")};
  color: ${(props) => props.theme.offWhite};
  border: none;
`;

const Button = ({
  children,
  type,
  onClick,
  buttonStyle,
  buttonSize,
  label,
}) => {
  return (
    <StyledButton
      primary={buttonStyle === "primary"}
      onClick={onClick}
      type={type}
    >
      {children}
    </StyledButton>
  );
};

export default Button;
