import React, { Component } from "react";
import { Mutation, Query } from "react-apollo";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
import Router from "next/router";
import Form from "./styles/Form";
import Error from "./ErrorMessage";

const SINGLE_SONG_QUERY = gql`
  query SINGLE_SONG_QUERY($id: ID!) {
    song(where: { id: $id }) {
      id
      title
      publisher
      altTitle
      fromFilm
      tribute
    }
  }
`;

const UPDATE_SONG_MUTATION = gql`
  mutation UPDATE_SONG_MUTATION(
    $id: ID!
    $title: String!
    $publisher: String
    $publishedDate: DateTime
    $altTitle: String
    $instrumental: Boolean
    $fromFilm: String
    $tribute: String
  ) {
    updateSong(
      id: $id
      title: $title
      publisher: $publisher
      publishedDate: $publishedDate
      altTitle: $altTitle
      instrumental: $instrumental
      fromFilm: $fromFilm
      tribute: $tribute
    ) {
      id
      title
      description
      price
    }
  }
`;

const UpdateSong = ({ id }) => {
  const { loading, error, data } = useQuery(SINGLE_SONG_QUERY, {
    variables: { id }
  });

  function updateSong() {
    console.log("running update song");
  }

  return (
    <Form onSubmit={updateSong}>
      <Error error={error} />
      <fieldset disabled={loading} aria-busy={loading}>
        <label htmlFor="title">
          Title
          <input
            type="text"
            id="title"
            name="title"
            placeholder="Title"
            required
            defaultValue={data.song.title}
            onChange={this.handleChange}
          />
        </label>

        <label htmlFor="publisher">
          Publisher
          <input
            type="text"
            id="publisher"
            name="publisher"
            placeholder="Title"
            defaultValue={data.song.publisher}
            onChange={this.handleChange}
          />
        </label>

        <label htmlFor="altTitle">
          Alt Title
          <input
            type="text"
            id="altTitle"
            name="altTitle"
            placeholder="Alt Title"
            defaultValue={data.song.altTitle}
            onChange={this.handleChange}
          />
        </label>

        <label htmlFor="fromFilm">
          From Film:
          <input
            type="text"
            id="fromFilm"
            name="fromFilm"
            placeholder="From Film"
            defaultValue={data.song.fromFilm}
            onChange={this.handleChange}
          />
        </label>

        <label htmlFor="tribute">
          Tribute To:
          <input
            type="text"
            id="tribute"
            name="tribute"
            placeholder="Tribute"
            defaultValue={data.song.tribute}
            onChange={this.handleChange}
          />
        </label>

        <button type="submit">Sav{loading ? "ing" : "e"} Changes</button>
      </fieldset>
    </Form>
  );
};

