import React from "react";
import Downshift, { resetIdCounter } from "downshift";
import Router from "next/router";
import { ApolloConsumer } from "react-apollo";
import gql from "graphql-tag";
import debounce from "lodash.debounce";
import { DropDown, DropDownItem, SearchStyles } from "./styles/DropDown";
import SongStyles from "./styles/song/songStyles";
import Title from "./styles/song/title";
import Song from "./styles/song/songStyles";

const SEARCH_SONGS_QUERY = gql`
  query SEARCH_SONGS_QUERY($searchTerm: String!) {
    songs(
      where: {
        OR: [
          { title_contains: $searchTerm }
          { altTitle_contains: $searchTerm }
          { fromFilm_contains: $searchTerm }
        ]
      }
    ) {
      id
      title
    }
  }
`;

function routeToSong(song) {
  song &&
    Router.push({
      pathname: "/song",
      query: {
        id: song.id
      }
    });
}

class AutoComplete extends React.Component {
  state = {
    songs: [],
    loading: false
  };
  onChange = debounce(async (e, client) => {
    this.setState({ loading: true });
    const response = await client.query({
      query: SEARCH_SONGS_QUERY,
      variables: { searchTerm: e.target.value }
    });
    this.setState({ songs: response.data.songs, loading: false });
  }, 350);

  render() {
    resetIdCounter();
    return (
      <SearchStyles>
        <Downshift
          onChange={routeToSong}
          itemToString={song => (song === null ? "" : song.title)}
        >
          {({
            getInputProps,
            getItemProps,
            isOpen,
            inputValue,
            highlightedIndex
          }) => (
            <div>
              <ApolloConsumer>
                {client => (
                  <input
                    {...getInputProps({
                      type: "search",
                      placeholder: "Search",
                      id: "search",
                      className: this.state.loading ? "loading" : "",
                      onChange: e => {
                        e.persist();
                        this.onChange(e, client);
                      }
                    })}
                  />
                )}
              </ApolloConsumer>
              {isOpen ? (
                <DropDown>
                  {this.state.songs.map((item, index) => (
                    <DropDownItem
                      {...getItemProps({ key: item.id, index, item })}
                      highlighted={index === highlightedIndex}
                    >
                      <Title>{item.title}</Title>
                    </DropDownItem>
                  ))}
                </DropDown>
              ) : null}
            </div>
          )}
        </Downshift>
      </SearchStyles>
    );
  }
}

export default AutoComplete;
