import React from "react";
import gql from "graphql-tag";
import { useQuery } from "@apollo/react-hooks";
import ErrorMessage from "./ErrorMessage";
import styled from "styled-components";
import Link from "next/link";
import { Card } from "primereact/card";

const SingleArtistStyles = styled.div`
  max-width: 1200px;
  margin: 2rem auto;
  box-shadow: ${(props) => props.theme.bs};
  display: grid;
  grid-auto-columns: 1fr;
  grid-auto-flow: column;
  min-height: 800px;
  .name {
    text-align: center;
  }
  .details {
    margin: 3rem;
    font-size: 2rem;
  }
`;

const TitleCard = styled(Card)`
  text-align: center;
`;

const SINGLE_ARTIST_QUERY = gql`
  query SINGLE_ARTIST_QUERY($id: ID!) {
    artist(where: { id: $id }) {
      id
      name
      songs {
        id
        title
      }
      albums {
        id
        title
      }
    }
  }
`;

const SingleArtist = ({ id }) => {
  const { loading, error, data } = useQuery(SINGLE_ARTIST_QUERY, {
    variables: { id },
  });
  if (error) return <ErrorMessage error={error} />;
  if (loading) return <p>Loading...</p>;
  if (!data.artist) return <p>No artist found for {id}</p>;
  const { name, songs, albums } = data.artist;
  return (
    <>
      <TitleCard title={name} />
    </>
    // <SingleArtistStyles>
    //   <div className="details">
    //     <h1 className="name">{name}</h1>
    //     {songs && (
    //       <h2>
    //         Songs {name} have recorded:{" "}
    //         {songs.map(song => (
    //           <p key={song.id}>
    //             <Link href={{ pathname: "song", query: { id: song.id } }}>
    //               <a>{song.title}</a>
    //             </Link>
    //           </p>
    //         ))}
    //       </h2>
    //     )}
    //     {albums && (
    //       <h2>
    //         Can be heard on the albums:{" "}
    //         {albums.map(album => (
    //           <p key={album.id}>
    //             <Link href={{ pathname: "album", query: { id: album.id } }}>
    //               <a>{album.title}</a>
    //             </Link>
    //           </p>
    //         ))}
    //       </h2>
    //     )}
    //   </div>
    // </SingleArtistStyles>
  );
};

export default SingleArtist;
