  //     break;
  //   case "artists":
  //     PAGINATION_QUERY = ARTISTS_PAGINATION_QUERY;
  //     break;
  //   case "composers":
  //     PAGINATION_QUERY = COMPOSERS_PAGINATION_QUERY;
  //     break;
  //   default:
  //     PAGINATION_QUERY = SONGS_PAGINATION_QUERY;
  // }

  // const { loading, error, data } = useQuery(PAGINATION_QUERY);
