import React from "react";
import gql from "graphql-tag";
import { useQuery } from "@apollo/react-hooks";
import ErrorMessage from "./ErrorMessage";
import styled from "styled-components";
import Link from "next/link";
import { Card } from "primereact/card";
import formatDate from "../lib/formatDate";

const SingleAlbumStyles = styled.div`
  max-width: 1200px;
  margin: 2rem auto;
  box-shadow: ${(props) => props.theme.bs};
  display: grid;
  grid-auto-columns: 1fr;
  grid-auto-flow: column;
  min-height: 800px;
  .title {
    text-align: center;
  }
  .details {
    margin: 3rem;
    font-size: 2rem;
  }
`;

const TitleCard = styled(Card)`
  text-align: center;
`;

const SongsCard = styled(Card)`
  display: grid;
  grid-template-columns: 1fr;
  .p-card-title {
    text-align: center;
  }
`;

const SongsList = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 60px;
  max-width: ${(props) => props.theme.maxWidth};
  margin: 0 auto;
  .p-card-title {
    text-align: left;
  }
`;

const SINGLE_ALBUM_QUERY = gql`
  query SINGLE_ALBUM_QUERY($id: ID!) {
    album(where: { id: $id }) {
      id
      title
      catalogNumber
      format
      info
      releaseDate
      recordLabel
      songs {
        id
        title
        composer {
          id
          name
        }
      }
      artists {
        id
        name
      }
      recordings {
        producer
        location
        completed
        albums {
          id
          title
        }
        song {
          id
          title
        }
      }
    }
  }
`;

const SingleAlbum = ({ id }) => {
  const { loading, error, data } = useQuery(SINGLE_ALBUM_QUERY, {
    variables: { id },
  });
  if (error) return <ErrorMessage error={error} />;
  if (loading) return <p>Loading...</p>;
  if (!data.album) return <p>No album found for {id}</p>;
  const {
    title,
    catalogNumber,
    format,
    info,
    releaseDate,
    recordLabel,
    songs,
    artists,
    recordings,
  } = data.album;
  const artistNames = artists.map((artist, index) => {
    let trailingChar = ", ";
    if (artists && index === artists.length - 2) {
      trailingChar = "& ";
    }
    if (artists && !artists[index + 1]) {
      trailingChar = "";
    }
    return (
      <span key={artist.id}>
        <Link href={{ pathname: "/artist", query: { id: artist.id } }}>
          <a>{artist.name} </a>
        </Link>
        {trailingChar}
      </span>
    );
  });
  return (
    <>
      <TitleCard title={title} subTitle={artistNames}>
        {catalogNumber && <h3>Catalog Number: {catalogNumber}</h3>}
        {format && <h3>Format: {format}</h3>}
        {releaseDate && <h3>Release Date: {formatDate(releaseDate)}</h3>}
        {recordLabel && <h3>Record Label: {recordLabel}</h3>}
        {info && <h3>Additional Info: {info}</h3>}
      </TitleCard>
      <SongsCard title={`Songs on ${title} (in alphabetical order):`}>
        <SongsList>
          {songs.map((song) => {
            const composerNames = song.composer.map((comp, index) => {
              let trailingChar = ", ";
              if (song.composer && index === song.composer.length - 2) {
                trailingChar = "& ";
              }
              if (song.composer && !song.composer[index + 1]) {
                trailingChar = "";
              }
              return (
                <span key={comp.id}>
                  <Link
                    href={{ pathname: "/composer", query: { id: comp.id } }}
                  >
                    <a>{comp.name} </a>
                  </Link>
                  {trailingChar}
                </span>
              );
            });
            const songLink = (
              <Link href={{ pathname: "/song", query: { id: song.id } }}>
                <a>{song.title}</a>
              </Link>
            );

            let songInfo = "";
            recordings.forEach((recording) => {
              console.log(
                `the recording song is ${recording.song.title}. the current song is ${song.title}`
              );
              if (recording.song.title === title) {
                console.log("ytes");
                songInfo +=
                  recording.producer + recording.location + recording.completed;
              }
            });
            console.log(songInfo);
            return (
              <Card title={songLink} subTitle={composerNames}>
                {songInfo}
              </Card>
            );
          })}
        </SongsList>
      </SongsCard>
    </>
    // <SingleAlbumStyles>
    //   <div className="details">
    //     <h1 className="title">{title}</h1>
    //     {catalogNumber && <h2>Catalog Number: {catalogNumber}</h2>}
    //     {format && <h2>Format: {format}</h2>}
    //     {releaseDate && <h2>Release Date: {releaseDate}</h2>}
    //     {recordLabel && <h2>Record Label: {recordLabel}</h2>}
    //     {info && <h2>Additional Info: {info}</h2>}
    //     {songs && (
    //       <h2>
    //         Songs:{" "}
    //         {songs.map((song) => (
    //           <p key={song.id}>
    //             <Link href={{ pathname: "song", query: { id: song.id } }}>
    //               <a>{song.title}</a>
    //             </Link>
    //           </p>
    //         ))}
    //       </h2>
    //     )}
    //     {artists && (
    //       <h2>
    //         Artists on this album:{" "}
    //         {artists.map((artist) => (
    //           <p key={artist.id}>
    //             <Link href={{ pathname: "artist", query: { id: artist.id } }}>
    //               <a>{artist.name}</a>
    //             </Link>
    //           </p>
    //         ))}
    //       </h2>
    //     )}
    //     {recordings && (
    //       <h2>
    //         Recordings:{" "}
    //         {recordings.map((recording) => (
    //           <div key={recording.id}>
    //             {recording.producer && <h2>Producer: {recording.producer}</h2>}
    //             {recording.location && <h2>Location: {recording.location}</h2>}
    //             {recording.completed && (
    //               <h2>Completed: {recording.completed}</h2>
    //             )}
    //           </div>
    //         ))}
    //       </h2>
    //     )}
    //   </div>
    // </SingleAlbumStyles>
  );
};

export default SingleAlbum;
