import React from "react";
import { useQuery } from "@apollo/react-hooks";

import formatDate from "../lib/formatDate";
import Link from "next/link";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { CURRENT_USER_QUERY } from "./Nav";

const Album = ({ album }) => {
  const { loading, error, data } = useQuery(CURRENT_USER_QUERY);
  const footer = data.me ? (
    <span>
      <Link href={{ pathname: "/update", query: { id: album.id } }}>
        <a>
          <Button
            label="Edit"
            icon="pi pi-pencil"
            style={{ marginRight: ".25em" }}
          />
        </a>
      </Link>
    </span>
  ) : null;
  const title = (
    <span>
      <Link href={{ pathname: "/album", query: { id: album.id } }}>
        <a>{album.title}</a>
      </Link>
    </span>
  );
  const artists =
    album.artists &&
    album.artists.map(
      artist =>
        `${(
          <Link
            key={artist.name}
            href={{ pathname: "/artist", query: { id: artist.id } }}
          >
            <a>{artist.name}</a>
          </Link>
        )}`
    );
  return (
    <Card
      title={title}
      footer={footer}
      subTitle={
        album.releaseDate
          ? `released ${formatDate(album.releaseDate)}`
          : "no release date information"
      }
    >
      {artists}
    </Card>
  );
};

