import React from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import styled from "styled-components";
import Composer from "./Composer";
import Pagination from "../components/Pagination";
import { perPage } from "../config";

const ALL_COMPOSERS_QUERY = gql`
  query ALL_COMPOSERS_QUERY($skip: Int = 0, $first: Int = ${perPage}) {
    composers(first: $first, skip: $skip, orderBy: name_ASC ) {
      id
      name
    }
  }
`;

const Center = styled.div`
  text-align: center;
`;

const ComposersList = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 60px;
  max-width: ${props => props.theme.maxWidth};
  margin: 0 auto;
`;

const Composers = ({ page }) => {
  const { loading, error, data } = useQuery(ALL_COMPOSERS_QUERY, {
    variables: { skip: page * perPage - perPage, first: perPage }
  });
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  const composers = data.composers.map(composer => (
    <Composer key={composer.id} composer={composer} />
  ));

  return (
    <Center>
      <Pagination page={page} model="composers" />
      <ComposersList>{composers}</ComposersList>
      <Pagination page={page} model="composers" />
    </Center>
  );
};

export default Composers;
