import React from "react";
import styled from "styled-components";

const STYLES = [
  "btn--primary--solid",
  "btn--warning--solid",
  "btn--danger--solid",
  "btn--success--solid",
  "btn--primary--outline",
  "btn--warning--outline",
  "btn--danger--outline",
  "btn--success--outline",
];

const SIZES = ["btn--medium", "btn-small"];

const StyledButton = styled.button`
  border-radius: 10px;
  cursor: pointer;
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-3px);
  }

  .btn--primary--solid {
    background-color: ${(props) => props.theme.blue};
    color: ${(props) => props.theme.offWhite};
    border: none;
  }
`;

const Button = ({
  children,
  type,
  onClick,
  buttonStyle,
  buttonSize,
  label,
}) => {
  const checkButtonStyle = STYLES.includes(buttonStyle)
    ? buttonStyle
    : STYLES[0];

  const checkButtonSize = SIZES.includes(buttonSize) ? buttonSize : SIZES[0];
  return (
    <StyledButton className="btn--primary--solid" onClick={onClick} type={type}>
      {children}
    </StyledButton>
  );
};

export default Button;
