import styled from "styled-components";

const Composer = styled.span`
  background: ${props => props.theme.red};
  /* transform: rotate(3deg); */
  color: white;
  font-weight: 600;
  padding: 5px;
  line-height: 1;
  font-size: 1.5rem;
  display: inline-block;
  position: absolute;
  /* top: -3px; */
  /* right: -3px; */
`;

export default Composer;
