import React, { useState } from "react";
import { useQuery } from "@apollo/react-hooks";
import Router from "next/router";
import { gql } from "apollo-boost";
import styled from "styled-components";
import Album from "./Album";
import Pagination from "../components/Pagination";
import { perPage } from "../config";
import { SelectButton } from "primereact/selectbutton";
import { selectButtonsAthruZWithNumber } from "../lib/selectButtons";
import { Dropdown } from "primereact/dropdown";
import { MultiList, Center } from "./Artists";

export const StyledDropdown = styled(Dropdown)`
  width: 50%;
  @media screen and (max-width: 600px) {
    width: 100%;
  }
`;

export const ALL_ALBUMS_QUERY = gql`
  query ALL_ALBUMS_QUERY($first: Int = ${perPage}, $skip: Int = 0, $sortBy: AlbumOrderByInput!) {
    albums(where: {OR: [{title_starts_with: ""}, {title_starts_with: "a"}, {title_starts_with: "b"}, {title_starts_with: "c"}, {title_starts_with: "d"}, {title_starts_with: "e"}, {title_starts_with: "f"}, {title_starts_with: "g"}, {title_starts_with: "h"}, {title_starts_with: "i"}, {title_starts_with: "j"}, {title_starts_with: "k"}, {title_starts_with: "l"}, {title_starts_with: "m"}, {title_starts_with: "n"}, {title_starts_with: "o"}, {title_starts_with: "p"}, {title_starts_with: "q"}, {title_starts_with: "r"}, {title_starts_with: "s"}, {title_starts_with: "t"}, {title_starts_with: "u"}, {title_starts_with: "v"}, {title_starts_with: "w"}, {title_starts_with: "x"}, {title_starts_with: "y"}, {title_starts_with: "z"}, ]} first: $first, skip: $skip, orderBy: $sortBy ) {
      id
      title
      recordLabel
      releaseDate
      format
      artists {
        id
        name
      }
    }
  }
`;

export const ALL_ALBUMS_BEGINNING_WITH_QUERY = gql`
  query ALL_ALBUMS_BEGINNING_WITH_QUERY($skip: Int = 0, $first: Int = ${perPage}, $letter: String, $sortBy: AlbumOrderByInput!) {
    albums(where: {title_starts_with: $letter}, first: $first, skip: $skip, orderBy: $sortBy) {
      id
      title
      recordLabel
      releaseDate
      format
      artists {
        id
        name
      }
    }
  }
`;

export const ALL_ALBUMS_BEGINNING_WITH_NUMBER_QUERY = gql`
  query ALL_ALBUMS_BEGINNING_WITH_NUMBER_QUERY($skip: Int = 0, $first: Int = ${perPage}, $sortBy: AlbumOrderByInput!) {
    albums(where: {OR: [{title_starts_with: "0"},{title_starts_with: "1"},{title_starts_with: "2"},{title_starts_with: "3"},{title_starts_with: "4"},{title_starts_with: "5"},{title_starts_with: "6"},{title_starts_with: "7"},{title_starts_with: "8"},{title_starts_with: "9"}]}, first: $first, skip: $skip, orderBy: $sortBy) {
      id
      title
      recordLabel
      releaseDate
      format
      artists {
        id
        name
      }
    }
  }
`;

const sortBySelectItems = [
  { label: "Title, ascending", value: "title_ASC" },
  { label: "Title, descending", value: "title_DESC" },
  { label: "Format, ascending", value: "format_DESC" },
  { label: "Format, descending", value: "format_ASC" },
  { label: "Release Date, ascending", value: "releaseDate_ASC" },
  { label: "Release Date, descending", value: "releaseDate_DESC" },
  { label: "Record Label, ascending", value: "recordLabel_ASC" },
  { label: "Record Label, descending", value: "recordLabel_DESC" },
];

const Albums = ({ page }) => {
  const [letter, setLetter] = useState("");
  const [sortBy, setSortBy] = useState("title_ASC");
  const {
    loading: allAlbumsLoading,
    error: allAlbumsError,
    data: allAlbumsData,
  } = useQuery(ALL_ALBUMS_QUERY, {
    variables: { skip: page * perPage - perPage, first: perPage, sortBy },
  });
  const {
    loading: albumsBeginningWithLoading,
    error: albumsBeginningWithError,
    data: albumsBeginningWithData,
  } = useQuery(ALL_ALBUMS_BEGINNING_WITH_QUERY, {
    variables: {
      skip: page * perPage - perPage,
      first: perPage,
      letter,
      sortBy,
    },
  });
  const {
    loading: albumsBeginningWithNumberLoading,
    error: albumsBeginningWithNumberError,
    data: albumsBeginningWithNumberData,
  } = useQuery(ALL_ALBUMS_BEGINNING_WITH_NUMBER_QUERY, {
    variables: {
      skip: page * perPage - perPage,
      first: perPage,
      sortBy,
    },
  });
  if (
    albumsBeginningWithLoading ||
    allAlbumsLoading ||
    albumsBeginningWithNumberLoading
  )
    return <p>Loading...</p>;
  if (
    albumsBeginningWithError ||
    allAlbumsError ||
    albumsBeginningWithNumberError
  )
    return <p>Error :(</p>;

  let albums;

  const albums2 =
    letter === "#"
      ? albumsBeginningWithNumberData.albums.map((album) => (
          <Album key={album.id} album={album} />
        ))
      : albumsBeginningWithData.albums.map((album) => (
          <Album key={album.id} album={album} />
        ));

  if (letter === "" || !letter) {
    console.log("letter equals null");
    albums = allAlbumsData.albums.map((album) => (
      <Album key={album.id} album={album} />
    ));
  } else if (letter === "#") {
    console.log("letter equals #");
    albums = albumsBeginningWithNumberData.albums.map((album) => (
      <Album key={album.id} album={album} />
    ));
  } else {
    console.log("letter equals some alphanumberic character");
    albums = albumsBeginningWithData.albums.map((album) => (
      <Album key={album.id} album={album} />
    ));
  }

  // const albums = !letter
  //   ? allAlbumsData.albums.map((album) => (
  //       <Album key={album.id} album={album} />
  //     ))
  //   : albumsBeginningWithData.albums.map((album) => (
  //       <Album key={album.id} album={album} />
  //     ));

  return (
    <Center>
      <SelectButton
        value={letter}
        options={selectButtonsAthruZWithNumber}
        onChange={(e) => {
          setLetter(e.value);
          Router.push({ pathname: "/albums", query: { page: 1 } });
        }}
      />
      <br />
      <StyledDropdown
        value={sortBy}
        options={sortBySelectItems}
        onChange={(e) => setSortBy(e.value)}
        placeholder="Sort By…"
      />
      <br />
      <Pagination page={page} model="albums" letter={letter} />
      <MultiList>{albums}</MultiList>
      <Pagination page={page} model="albums" letter={letter} />
    </Center>
  );
};

export default Albums;
