import React, { useState } from "react";
import { useQuery, useMutation } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import formatDate from "../lib/formatDate";
import Link from "next/link";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { CURRENT_USER_QUERY } from "./Nav";
import { Checkbox } from "primereact/checkbox";
import styled from "styled-components";

const StyledFooter = styled.div`
  display: flex;
  justify-content: space-evenly;
`;

const StyledFlexStart = styled.div`
  align-self: center;
`;

const StlyedFlexEnd = styled.div`
  align-self: flex-end;
`;

export const ADD_TO_LOCAL_ID_SELECTION_MUTATION = gql`
  mutation($id: String!) {
    addToLocalIdSelection(id: $id) @client
  }
`;
export const REMOVE_FROM_LOCAL_ID_SELECTION_MUTATION = gql`
  mutation($id: String!) {
    removeFromLocalIdSelection(id: $id) @client
  }
`;

export const LOCAL_STATE_QUERY = gql`
  query {
    selectedIds @client
  }
`;

const Album = ({ album, addToCheckedItemsList, checkedItems }) => {
  const [checked, setIsChecked] = useState(false);
  const { loading, error, data } = useQuery(CURRENT_USER_QUERY);
  const {
    loading: localStateQueryLoading,
    error: localStateQueryError,
    data: localStateQueryData,
  } = useQuery(LOCAL_STATE_QUERY);
  const [
    addToLocalIdSelectionMutation,
    {
      loading: addToLocalIdSelectionMutationLoading,
      error: addToLocalIdSelectionMutationError,
    },
  ] = useMutation(ADD_TO_LOCAL_ID_SELECTION_MUTATION);
  const [
    removeFromLocalIdSelectionMutation,
    {
      loading: removeFromLocalIdSelectionLoading,
      error: removeFromLocalIdSelectionError,
    },
  ] = useMutation(ADD_TO_LOCAL_ID_SELECTION_MUTATION);

  function handleChecked(e) {
    if (e.checked) {
      addToLocalIdSelectionMutation({ variables: { id: album.id } });
    }
    if (!e.checked) {
      console.log("you done unchecked this box");
    }
  }

  function isChecked(id) {
    return localStateQueryData.selectedIds.includes(id);
  }

  const footer = data.me ? (
    <StyledFooter>
      <StyledFlexStart>
        <Link href={{ pathname: "/updatealbum", query: { id: album.id } }}>
          <a>
            <Button
              label="Edit"
              icon="pi pi-pencil"
              style={{ marginRight: ".25em" }}
            />
          </a>
        </Link>
      </StyledFlexStart>
      <StlyedFlexEnd>
        <Checkbox
          onChange={(e) => handleChecked(e)}
          checked={isChecked(album.id)}
        />
      </StlyedFlexEnd>
    </StyledFooter>
  ) : null;
  const title = (
    <span>
      <Link href={{ pathname: "/album", query: { id: album.id } }}>
        <a>{album.title || "no title"}</a>
      </Link>
    </span>
  );
  const artists =
    album.artists &&
    album.artists.map((artist, index) => (
      <span key={artist.name}>
        <Link href={{ pathname: "/artist", query: { id: artist.id } }}>
          <a>{artist.name}</a>
        </Link>
        {album.artists.length === index + 1 ? " " : ", "}
      </span>
    ));
  return (
    <Card
      title={title}
      footer={footer}
      subTitle={
        album.releaseDate
          ? `released ${formatDate(album.releaseDate)}`
          : "no release date information"
      }
    >
      {album.single && <p>Single</p>}
      {album.format && <p>Format: {album.format}</p>}
      {album.catalogNumber && <p>Catalog #: {album.catalogNumber}</p>}
      {artists}
    </Card>
  );
};

export default Album;
