import React from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import styled from "styled-components";
import Album from "./Album";
import Pagination from "../components/Pagination";
import { perPage } from "../config";

const ALL_ALBUMS_QUERY = gql`
  query ALL_ALBUMS_QUERY($skip: Int = 0, $first: Int = ${perPage}) {
    albums(first: $first, skip: $skip, orderBy: title_ASC ) {
      id
      title
      recordLabel
      releaseDate
      format
      artists {
        id
        name
      }
    }
  }
`;

const AlbumsList = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr;
  grid-gap: 60px;
  max-width: ${props => props.theme.maxWidth};
  margin: 0 auto;
`;

const Center = styled.div`
  text-align: center;
`;

const Albums = ({ page }) => {
  const { loading, error, data } = useQuery(ALL_ALBUMS_QUERY, {
    variables: { skip: page * perPage - perPage, first: perPage }
  });
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  const albums = data.albums.map(album => (
    <Album key={album.id} album={album} />
  ));

  return (
    <Center>
      <Pagination page={page} model="albums" />
      <AlbumsList>{albums}</AlbumsList>
      <Pagination page={page} model="albums" />
    </Center>
  );
};

export default Albums;
