import React from "react";
import Title from "./styles/song/title";
import SongStyles from "./styles/song/songStyles";
import Composer from "./styles/song/composer";
import Link from "next/link";

const Song = ({ song }) => {
  const composerNames = song.composer.map(comp => comp.name).join(", ");
  return (
    <SongStyles>
      <Title>
        <Link
          href={{
            pathname: "/song",
            query: { id: song.id }
          }}
        >
          <a>{song.title}</a>
        </Link>
      </Title>
      {song.composer && <Composer>{composerNames}</Composer>}
    </SongStyles>
  );
};

export default Song;
