import React from "react";
import Downshift from "downshift";
import Router from "next/router";
import { ApolloConsumer } from "react-apollo";
import gql from "graphql-tag";
import debounce from "lodash.debounce";
import { DropDown, DropDownItem, SearchStyles } from "./styles/DropDown";
import SongStyles from "./styles/song/songStyles";
import Title from "./styles/song/title";

const SEARCH_SONGS_QUERY = gql`
  query SEARCH_SONGS_QUERY($searchTerm: String!) {
    songs(
      where: {
        OR: [
          { title_contains: $searchTerm }
          { altTitle_contains: $searchTerm }
          { fromFilm_contains: $searchTerm }
        ]
      }
    ) {
      id
      title
    }
  }
`;

class AutoComplete extends React.Component {
  state = {
    songs: [],
    loading: false
  };
  onChange = debounce(async (e, client) => {
    this.setState({ loading: true });
    const response = await client.query({
      query: SEARCH_SONGS_QUERY,
      variables: { searchTerm: e.target.value }
    });
    this.setState({ songs: response.data.songs, loading: false });
  }, 350);

  render() {
    return (
      <SearchStyles>
        <Downshift>
          {({
            getInputProps,
            getItemProps,
            isOpen,
            inputValue,
            highlightedIndex
          }) => (
            <div>
              <ApolloConsumer>
                {client => (
                  <input
                    {...getInputProps({
                      onChange: e => {
                        e.persist();
                        this.onChange(e, client);
                      },
                      type: "search",
                      placeholder: "Search",
                      id: "search",
                      className: this.state.loading ? "loading" : ""
                    })}
                  />
                )}
              </ApolloConsumer>
              {isOpen && (
                <DropDown>
                  {this.state.songs.map(song => (
                    <DropDownItem {...getItemProps(song)} key={song.id}>
                      <Title>{song.title}</Title>
                    </DropDownItem>
                  ))}
                </DropDown>
              )}
            </div>
          )}
        </Downshift>
      </SearchStyles>
    );
  }
}

export default AutoComplete;
