import React from "react";
import Title from "./styles/song/title";
import AlbumStyles from "./styles/song/songStyles";
import { useQuery } from "@apollo/react-hooks";
// import Composer from "./styles/song/composer";
import Link from "next/link";
import PleaseSignIn from "./PleaseSignIn";
// import DeleteSong from "./DeleteSong";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { CURRENT_USER_QUERY } from "./Nav";

const Album = ({ album }) => {
  const { loading, error, data } = useQuery(CURRENT_USER_QUERY);
  const footer = data.me ? (
    <span>
      <Link href={{ pathname: "/update", query: { id: album.id } }}>
        <a>
          <Button
            label="Edit"
            icon="pi pi-pencil"
            style={{ marginRight: ".25em" }}
          />
        </a>
      </Link>
    </span>
  ) : null;
  const title = (
    <span>
      <Link href={{ pathname: "/album", query: { id: album.id } }}>
        <a>{album.title}</a>
      </Link>
    </span>
  );

  return <Card title={title} footer={footer} subTitle={album.releaseDate} />;
};

