import React from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import styled from "styled-components";

const ALL_SONGS_QUERY = gql`
  query ALL_SONGS_QUERY {
    songs {
      id
      title
      publisher
      publishedDate
    }
  }
`;

const Center = styled.div`
  text-align: center;
`;

const SongsList = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 60px;
  max-width: ${props => props.theme.maxWidth};
  margin: 0 auto;
`;

const Songs = ({}) => {
  const { loading, error, data } = useQuery(ALL_SONGS_QUERY);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  const songs = data.songs.map(song => <p key={song.id}>{song.title}</p>);

  return (
    <Center>
      <p>
        <b>I found {songs.length} songs.</b>
      </p>
      <SongsList>{songs}</SongsList>
    </Center>
  );
};

export default Songs;
