import React from "react";
import { useQuery } from "@apollo/react-hooks";
import Title from "./styles/song/title";
import SongStyles from "./styles/song/songStyles";
import Composer from "./styles/song/composer";
import Link from "next/link";
import DeleteSong from "./DeleteSong";
import SigninCheck from "./PleaseSignIn";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { CURRENT_USER_QUERY } from "./Nav";
import "primereact/resources/themes/nova-light/theme.css";
import "primereact/resources/primereact.min.css";
import "primeicons/primeicons.css";

const Song = ({ song }) => {
  const { loading, error, data } = useQuery(CURRENT_USER_QUERY);
  const composerNames = song.composer.map(comp => comp.name).join(", ");
  const footer = data.me ? (
    <span>
      <Link href={{ pathname: "/song", query: { id: song.id } }}>
        <a>
          <Button
            label="Edit"
            icon="pi pi-check"
            style={{ marginRight: ".25em" }}
          />
        </a>
      </Link>
      <Button
        label="Delete"
        icon="pi pi-times"
        className="p-button-secondary"
      />
    </span>
  ) : null;
  return <Card title={song.title} subTitle={composerNames} footer={footer} />;
