  //         }}
  //       >
  //         <a>{song.title}</a>
  //       </Link>
  //     </Title>
  //     {song.composer && <Composer>{composerNames}</Composer>}
  //     <SigninCheck>
  //       <div className="buttonList">
  //         <Link href={{ pathname: "update", query: { id: song.id } }}>
  //           <a>Edit Song</a>
  //         </Link>
  //         <DeleteSong id={song.id}>Delete Song</DeleteSong>
  //       </div>
  //     </SigninCheck>
  //   </SongStyles>
  // );
};

export default Song;
