import React from "react";
import Downshift, { resetIdCounter } from "downshift";
import Router from "next/router";
import { ApolloConsumer } from "react-apollo";
import gql from "graphql-tag";
import debounce from "lodash.debounce";
import { DropDown, DropDownItem, SearchStyles } from "./styles/DropDown";
import SongStyles from "./styles/song/songStyles";
import Title from "./styles/song/title";
import Song from "./styles/song/songStyles";

const SEARCH_SONGS_QUERY = gql`
  query SEARCH_SONGS_QUERY($searchTerm: String!) {
    songs(
      where: {
        OR: [
          { title_contains: $searchTerm }
          { altTitle_contains: $searchTerm }
          { fromFilm_contains: $searchTerm }
        ]
      }
    ) {
      id
      title
    }
  }
`;
const SEARCH_ARTISTS_QUERY = gql`
  query SEARCH_ARTISTS_QUERY($searchTerm: String!) {
    artists(where: { OR: [{ name_contains: $searchTerm }] }) {
      id
      name
    }
  }
`;

const SEARCH_ALBUMS_QUERY = gql`
  query SEARCH_ALBUMS_QUERY($searchTerm: String!) {
    artists(where: { OR: [{ title_contains: $searchTerm }] }) {
      id
      title
    }
  }
`;
const SEARCH_COMPOSERS_QUERY = gql`
  query SEARCH_COMPOSERS_QUERY($searchTerm: String!) {
    composers(where: { OR: [{ name_contains: $searchTerm }] }) {
      id
      name
    }
  }
`;

function routeToItem(route, item) {
  item &&
    Router.push({
      pathname: `/${route}`,
      query: {
        id: item.id
      }
    });
}

function returnQuery(item) {
  console.log(item);
  if (item === "song") {
    return SEARCH_SONGS_QUERY;
  }
  if (item === "album") {
    return SEARCH_ALBUMS_QUERY;
  }
  if (item === "artist") {
    return SEARCH_ARTISTS_QUERY;
  }
  if (item === "composer") {
    return SEARCH_COMPOSERS_QUERY;
  }
}

class AutoComplete extends React.Component {
  state = {
    items: [],
    loading: false,
    item: "song"
  };
  handleSelectChange(event) {
    this.setState({ item: event.target.value });
  }
  onChange = debounce(async (e, client) => {
    this.setState({ loading: true });
    const response =
      e.target.value &&
      (await client.query({
        query: returnQuery(this.state.item),
        variables: { searchTerm: e.target.value }
      }));
    e.target.value &&
      this.setState({
        items:
          response.data.songs ||
          response.data.albums ||
          response.data.composers ||
          response.data.artists,
        loading: false
      });
  }, 350);

  render() {
    resetIdCounter();
    return (
      <SearchStyles>
        <Downshift
          onChange={item => routeToItem(this.state.item, item)}
          itemToString={item => (item === null ? "" : item.title || item.name)}
        >
          {({
            getInputProps,
            getItemProps,
            isOpen,
            inputValue,
            highlightedIndex
          }) => (
            <div>
              <ApolloConsumer>
                {client => (
                  <>
                    <input
                      {...getInputProps({
                        type: "search",
                        placeholder: "Search",
                        id: "search",
                        className: this.state.loading ? "loading" : "",
                        onChange: e => {
                          e.persist();
                          this.onChange(e, client);
                        }
                      })}
                    />
                    <select
                      value={this.state.item}
                      onChange={this.handleSelectChange}
                    >
                      <option value="song">Song</option>
                      <option value="artist">Artist</option>
                      <option value="album">Album</option>
                      <option value="composer">Composer</option>
                    </select>
                  </>
                )}
              </ApolloConsumer>
              {isOpen ? (
                <DropDown>
                  {this.state.items.map((item, index) => (
                    <DropDownItem
                      {...getItemProps({ key: item.id, index, item })}
                      highlighted={index === highlightedIndex}
                    >
                      <Title>{item.title || item.name}</Title>
                    </DropDownItem>
                  ))}
                </DropDown>
              ) : null}
            </div>
          )}
        </Downshift>
      </SearchStyles>
    );
  }
}

export default AutoComplete;
