import React from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import styled from "styled-components";

const ALL_COMPOSERS_QUERY = gql`
  query ALL_COMPOSERS_QUERY {
    composers {
      id
      name
    }
  }
`;

const Center = styled.div`
  text-align: center;
`;

const Albums = ({}) => {
  const { loading, error, data } = useQuery(ALL_COMPOSERS_QUERY);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  const composers = data.composers.map(composer => (
    <p key={composer.id}>{composer.name}</p>
  ));

  return (
    <Center>
      <p>
        <b>I found {composers.length} composers.</b>
      </p>
      {composers}
    </Center>
  );
};

export default Albums;
